import React from "react";
import { useRef } from "react";
import '../css/Home.css'
import '../css/product.css'
import axios from 'axios';

function Product({ src, name, description, price }) {
    const hiddenItemsRef = useRef([]);
    const showMore = () => {
        hiddenItemsRef.current.forEach(item => {
            item.classList.remove('hidden'); // Bỏ lớp ẩn cho các sản phẩm ẩn
        });
        document.getElementById('Show').style.display = 'none'; // Ẩn nút sau khi hiển thị
    };
    return (
        <div className="container-fluid">
            <div className="row">
            <div className="col-md-3">
            <div className="the">
                <img className="productimg" src={src} alt={name} />
                <h2>{name}</h2>
                <p className="mota">{description}</p>
                <p className="giatien">{price} VND</p>
            </div>
        </div>
                </div>
            </div>
               
    )


}
export default Product;
