import React, { useState } from 'react'; // Nhớ import useState
import { BrowserRouter as Router, Route, Routes } from 'react-router-dom';
import './App.css';
import Home from './page/Home';
import Product from './page/product';
import About from './page/About';
import Navbar from './component/Navbar';
import Signup from './component/sign'; 
import LoginSignup from './component/LoginSignup';
import Cart from './page/Cart';

function App() {
  const [cartItems, setCartItems] = useState([]); // Khai báo state cho giỏ hàng

  const cartCount = cartItems.reduce((acc, item) => acc + item.quantity, 0); // Đếm số sản phẩm trong giỏ hàng
  const handleAddToCart = (product) => {
    setCartItems(prevItems => {
        const existingItem = prevItems.find(item => item.id === product.id); // Kiểm tra xem sản phẩm đã có trong giỏ hàng chưa
        if (existingItem) {
            return prevItems.map(item =>
                item.id === product.id ? { ...item, quantity: item.quantity + 1 } : item
            );
        } else {
            return [...prevItems, { ...product, quantity: 1 }]; // Thêm sản phẩm mới
        }
    });
};

  return (
    <div className="App">
      <Router>
        <Navbar cartCount={cartCount} /> {/* Truyền cartCount cho Navbar */}
        <Routes>
        <Route path="/" element={<Home handleAddToCart={handleAddToCart} />} />
        <Route path="/product" element={<Product handleAddToCart={handleAddToCart} setCartItems={setCartItems} />}/>} />
          <Route path="/about" element={<About />} />
          <Route path="/signup" element={<Signup />} />
          <Route path="/login" element={<LoginSignup />} />
          <Route path="/cart" element={<Cart cartItems={cartItems} setCartItems={setCartItems} />} />
        </Routes>
      </Router>
    </div>
  );
}

export default App;
