import React from "react";
import { useRef } from "react";
import { useState } from "react";
import { useEffect } from "react";
import '../css/Home.css'
import '../css/product.css'
import axios from 'axios';

function Product() {
    const [products, setProducts] = useState([]);
    const hiddenItemsRef = useRef([]);

    useEffect(() => {
        // Gọi API để lấy dữ liệu sản phẩm
        axios.get('https://localhost:7256/api/Sanpham')
            .then(response => {
                setProducts(response.data); // Lưu dữ liệu vào state
            })
            .catch(error => {
                console.error("Lỗi khi lấy dữ liệu sản phẩm:", error);
            });
    }, []);

    const showMore = () => {
        hiddenItemsRef.current.forEach(item => {
            if (item) item.classList.remove('hidden');
        });
        document.getElementById('Show').style.display = 'none';
    };

    return (
        <div className="container-fluid">
            <div className="row">
                <h1 className="mb-5">Bộ sưu tập</h1>
                {products.map((product, index) => (
                    <div 
                        key={product.productId} 
                        className={`col-md-3 ${index >= 8 ? 'hidden' : ''}`} 
                        ref={el => hiddenItemsRef.current[index - 8] = el}
                    >
                        <div className="the">
                            <img 
                                className="productimg" 
                                src={product.imageUrl || "https://i.ebayimg.com/images/g/HDEAAOSwArFc9qBa/s-l400.jpg"} 
                                alt={product.productName} 
                            />
                            <h2>{product.productName}</h2>
                            <p className="mota">{product.moTa}</p>
                            <p className="giatien">{product.gia} VND</p>
                        </div>
                    </div>
                ))}
                
                    <button id="Show" onClick={showMore} className="mt-3 btn btn-primary">Show more</button>
           
            </div>
        </div>
    );
}
export default Product;
