import React, { useRef } from "react";
import { useState } from "react";
import axios from 'axios';
import '../css/Home.css';
import '../css/product.css';

function Product({ cartItems, setCartItems }) {
    const hiddenItemsRef = useRef([]);
    const [products, setProducts] = React.useState([]);
    const [showModal, setShowModal] = useState(false);
    const [quantity, setQuantity] = useState(1);
    const [selectedProduct, setSelectedProduct] = useState(null); // State for the selected product

    // Fetch data from API
    React.useEffect(() => {
        const fetchData = async () => {
            try {
                const response = await axios.get('https://localhost:7256/api/Sanpham');
                setProducts(response.data);
            } catch (error) {
                console.error("Error fetching data", error);
            }
        };

        fetchData();
    }, []);

    const showMore = () => {
        hiddenItemsRef.current.forEach(item => {
            if (item) {
                item.classList.remove('hidden');
            }
        });
        document.getElementById('Show').style.display = 'none';
    };

    const handleShowModal = (product) => {
        setSelectedProduct(product); // Set the selected product
        setShowModal(true);
    };

    const handleCloseModal = () => setShowModal(false);

    const handleQuantityChange = (e) => {
        const value = Math.max(1, parseInt(e.target.value) || 1);
        setQuantity(value);
    };

    const handleAddToCart = (product) => {
        setCartItems(prevItems => {
            const existingItem = prevItems.find(item => item.productId === product.productId);
            if (existingItem) {
                return prevItems.map(item =>
                    item.productId === product.productId ? { ...item, quantity: item.quantity + 1 } : item
                );
            } else {
                return [...prevItems, { ...product, quantity: 1 }];
            }
        });
    };

    return (
        <div className="container-fluid">
            <div className="row">
                <h1 className="mb-5">Bộ sưu tập</h1>
                {products.map((product, index) => (
                    <div 
                        key={product.productId} 
                        className={`col-md-3 ${index >= 8 ? 'hidden' : ''}`} 
                        ref={el => index >= 8 && (hiddenItemsRef.current[index - 8] = el)}
                    >
                        <div className="card card-border product-card mb-4">
                            <div className="product-image">
                                <img 
                                    className="card-img-top" 
                                    src={product.imageUrl || "https://i.ebayimg.com/images/g/HDEAAOSwArFc9qBa/s-l400.jpg"} 
                                    alt={product.productName} 
                                />
                                <button 
                                    className="btn btn-hover-orange buy-now" 
                                    onClick={() => handleShowModal(product)}
                                >
                                    Mua Ngay
                                </button>
                            </div>
                            <div className="card-body">
                                <h5 className="card-title">{product.productName}</h5>
                                <p className="card-text">{product.moTa}</p>
                                <p className="card-text">{product.gia} VND</p>
                            </div>
                        </div>
                    </div>
                ))}
            </div>

            {/* Modal for the selected product */}
            {showModal && selectedProduct && (
                <div className="modal fade show" style={{ display: 'block' }} tabIndex="-1" role="dialog">
                    <div className="modal-dialog modal-lg">
                        <div className="modal-content">
                            <div className="modal-header">
                                <h5 className="modal-title">{selectedProduct.productName}</h5>
                                <button type="button" className="btn-close" onClick={handleCloseModal}></button>
                            </div>
                            <div className="modal-body">
                                <div id="more-img" className="carousel slide" data-bs-ride="carousel">
                                    <div className="carousel-inner">
                                        <div className="carousel-item active">
                                            <img src={selectedProduct.imageUrl || "https://i.ebayimg.com/images/g/HDEAAOSwArFc9qBa/s-l400.jpg"} className="d-block w-100 img-thumbnail" alt="Product Image 1" />
                                        </div>
                                    </div>
                                </div>
                                <p className="product-description">{selectedProduct.moTa}</p>
                                <p className="product-price">Giá: {selectedProduct.gia} VND</p>
                                <div className="quantity-control">
                                    <label htmlFor="quantity" className="form-label">Số lượng:</label>
                                    <input type="number" id="quantity" className="form-control" min="1" value={quantity} onChange={handleQuantityChange} />
                                </div>
                            </div>
                            <div className="modal-footer">
                                <button type="button" className="btn btn-secondary" onClick={handleCloseModal}>Đóng</button>
                                <button type="button" className="btn btn-primary" onClick={() => handleAddToCart(selectedProduct)}>Mua Ngay</button>
                            </div>
                        </div>
                    </div>
                </div>
            )}
        </div>
    );
}

export default Product;
